import React, { useState } from 'react'
import './Tictoc.css'
import roundshape from '../assets/circle.png'
import crossicon from '../assets/tic.png'

let data= ["", "","","","","","","",""];



const Tictoc = () => {
  let [count, setcount] = useState(0);
  let [lock, setlock] = useState(false);

  const toggle = (e,num)=>{
if(lock){
  return 0;
}
if(count%2===0){
e.target.innerHTML = `<img src='${crossicon}' >`;
data[num]="x";
setcount(++count)
}
else{
  e.target.innerHTML = `<img src='${roundshape}' >`;
data[num]="o";
setcount(++count)

}
checkwin()
  }

const checkwin = ()=>{
  if(data[0]===data[1] && data[1]===data[2] && data[2]!=='')
{
won(data)
}
else if(data[3]===data[4] && data[4]===data[5] && data[5]!==''){
  won(data)
}
else if(data[6]===data[7] && data[7]===data[8] && data[8]!==''){
  won(data)
}
else if(data[0]===data[3] && data[3]===data[6] && data[6]!==''){
  won(data)
}
else if(data[1]===data[4] && data[4]===data[7] && data[7]!==''){
  won(data)
}
else if(data[2]===data[5] && data[5]===data[8] && data[8]!==''){
  won(data)
}
else if(data[0]===data[4] && data[4]===data[8] && data[8]!==''){
  won(data)
}
else if(data[0]===data[1] && data[1]===data[2] && data[2]!==''){
  won(data)
}
else if(data[2]===data[4] && data[4]===data[6] && data[6]!==''){
  won(data)
}

}

const won =(winner) =>{
  setlock(true)
}

  return (
    <div className='container'>
      <h1 className='title'> Tic Toe Toe with <span>React</span> </h1>
      <div className="board">
<div className="row1">
  <div className="boxex" onClick={(e)=>{toggle(e,0)}}></div>
  <div className="boxex" onClick={(e)=>{toggle(e,1)}}></div>
  <div className="boxex" onClick={(e)=>{toggle(e,2)}}></div>
</div>
<div className="row2">
  <div className="boxex" onClick={(e)=>{toggle(e,3)}}></div>
  <div className="boxex" onClick={(e)=>{toggle(e,4)}}></div>
  <div className="boxex" onClick={(e)=>{toggle(e,5)}}></div>
</div>
<div className="row3">
  <div className="boxex" onClick={(e)=>{toggle(e,6)}}></div>
  <div className="boxex" onClick={(e)=>{toggle(e,7)}}></div>
  <div className="boxex" onClick={(e)=>{toggle(e,8)}}></div>
</div>
      </div>
      <button className='reset'>Reset</button>
    </div>
  )
}

export default Tictoc
